import React, { useRef, useState } from "react";
import { useDispatch } from "react-redux";
import { createTransaction } from "../../../redux/actions/actionCreator";
import toast from "toasted-notes";
import Cookies from "universal-cookie";

import { Container } from "../../../styledComponents/Dashboard/Modals/createTransactionModal";

const CreateTransaction = ({ state, setState }) => {
  const validateinput = () => {
    const {
      transaction_name,
      transaction_ammount,
      transaction_type,
    } = userValue;
    if (
      transaction_name === "" ||
      transaction_ammount === "" ||
      transaction_type === ""
    ) {
      toast.notify("All values are required");
    } else if (
      transaction_name &&
      transaction_ammount &&
      transaction_type === ""
    ) {
      toast.notify("values can never be empty ");
    } else {
      let data = {
        ...userValue,
        transaction_by: cookies.get("user info")[0].id,
      };

      dispatch(createTransaction(data, cookies.get("login")));
      // setTimeout(toggleModal, 2000);
    }
  };
  return (
    <Container>
      <div className="modalWrapper">
        <div className="modalContent">
          <div className="closeModal">&times;</div>
          <h1 className="header">Create Transaction</h1>
          <div className="userValue">
            <form>
              <label>transaction name</label>
              <input
                type="text"
                name="transaction_name"
                placeholder="Transaction Name"
              />
              <label>transaction amount</label>
              <input
                type="number"
                name="transaction_ammount"
                placeholder="Transaction Amount"
              />
              <label>Transaction type</label>
              <select name="transaction_type">
                <option value="select">Select Transaction</option>
                <option value="income">Income</option>
                <option value="expenditure">Expenditure</option>
              </select>
              <div className="buttons">
                <button
                  className="create"
                  onClick={(e) => {
                    e.preventDefault();
                    validateinput();
                  }}
                >
                  Create Transaction
                </button>
                <button
                  className="cancel"
                  onClick={(e) => {
                    e.preventDefault();
                    toggleModal();
                  }}
                >
                  Cancel
                </button>
              </div>
            </form>
          </div>
        </div>
      </div>
    </Container>
  );
};

export default CreateTransaction;
