import styled from "styled-components";
import { Link } from "react-router-dom";
import image from "../images/micheile-henderson-ZVprbBmT8QA-unsplash.jpg";

const media = {
  mobile: "@media(max-width: 550px)",
};

export const Container = styled.div``;

//navbar links
export const StyledLink = styled(Link)`
  display: inline;
  margin-right: 1em;
  color: white;
`;

export const Header = styled.header`
  nav {
    background-color: rgba(0, 0, 0, 0.7);
    color: white;
    padding: 1.5em 0;
  }

  nav h1 {
    display: inline;
    margin-left: 1.5em;
    font-size: 1.5rem;
    font-weight: normal;
    text-transform: uppercase;
    letter-spacing: 0.5em;
  }
  nav h1 a {
    text-decoration: none;
    color: white;
    cursor: pointer;
  }

  nav .nav-container {
    list-style: none;
    float: right;
  }
`;

/*
side bar menu for mobile devices
*/
export const MobileNavContainer = styled.div``;
export const MobileNav = styled(Link)``;

/*
main content area which contains the login form
and the sign up page
*/
export const Main = styled.main``;

//Footer Page
export const Footer = styled.footer``;
