import styled from "styled-components";
// import { Link } from "react-router-dom";

export const Nav = styled.div``;

export const LeftSection = styled.div`
  float: left;
  width: 60%;
  height: 100%;

  h4 {
    font-size: 0.9em;
    font-weight: normal;
    margin-left: 1em;
    letter-spacing: 5px;
  }
  p {
    font-size: 0.8em;
    margin-left: 1em;
  }
`;

export const RightSection = styled.div`
  // background-color: black;
  float: left;
  width: 40%;
  height: 100%;
  // color: #fff;

  img {
    height: 1.5em;
    margin-top: 2em;
    margin-left: 14em;
  }
  img:hover {
    cursor: pointer;
  }
`;
