import styled from "styled-components";

export const Container = styled.div`
  //background-color: red;
  color: black;
  position: relative;

  .modalWrapper {
  }

  .modalContent {
    position: absolute;
    width: 100vw;
    height: 45vh;
    left: calc(-59vw + 40%);
    top: -3.5em;
    background: red;
    color: #000;
    z-index: 10;
    border-radius: 10px;
  }
  .modalContent > .header {
    text-align: center;
    font-size: 1.5em;
  }
  .userValue > form > label {
    text-transform: uppercase;
    display: block;
    text-align: center;
    margin: 1em 0;
  }
  .userValue > form > input {
    display: block;
    padding: 0.9em;
    margin: 0 auto;
    width: 80%;
    border-radius: 0.5em;
    border: none;
  }
`;
