import styled from "styled-components";
// import { Link } from "react-router-dom";
export const BalanceStats = styled.div`
  background-color: #fff;
  grid-area: balanceStat;
  box-shadow: 5px 5px 15px rgba(0, 0, 0, 0.9);

  h4 {
    display: inline;
    font-size: 1.2em;
    font-weight: normal;
    margin: 5em;
  }
`;
