import React, { useState, useEffect } from "react";
import ClipLoader from "react-spinners/ClipLoader";
import Cookies from "universal-cookie";
import { useDispatch, useSelector } from "react-redux";
import { allActivities } from "../../redux/actions/dashboard/activities";
import icon from "../../images/more-512.webp";
import { Activities as Activity } from "../../styledComponents/Dashboard/Activities";
const Activities = (props) => {
  //user tokens
  const cookies = new Cookies();
  let getToken = cookies.get("auth_token");
  let getUserInfo = cookies.get("user_info")[0];

  const userData = {
    token: getToken,
    id: getUserInfo.id,
  };

  const [state, setState] = useState({
    active: false,
    Transations: true,
    Earnings: false,
    Expenses: false,
  });

  //border css
  const stye = {
    borderBottom: "2px solid black",
  };

  const dispatch = useDispatch();

  const getAllTransacrtions = () => {
    setState((prevState) => ({
      ...prevState,
      Transations: true,
      Earnings: false,
      Expenses: false,
    }));
    dispatch(allActivities(getToken));
  };

  const getEarnings = () => {
    setState((prevState) => ({
      ...prevState,
      Transations: false,
      Earnings: true,
      Expenses: false,
    }));
    // dispatch(getEarningsData(userData));
  };

  const getExpenses = () => {
    setState((prevState) => ({
      ...prevState,
      Transations: false,
      Earnings: false,
      Expenses: true,
    }));
    // dispatch(Expences(userData));
  };
  return (
    <Activity>
      <div className="top-bar">
        <img src={icon} alt="options icon" />
        <h3>Recent Transactions</h3>

        <ul>
          <li
            style={state.Transations ? stye : null}
            onClick={() => {
              getAllTransacrtions();
            }}
          >
            Transactions
          </li>
          <li
            style={state.Earnings ? stye : null}
            onClick={() => {
              getEarnings();
            }}
          >
            Earnings
          </li>
          <li
            style={state.Expenses ? stye : null}
            onClick={() => {
              getExpenses();
            }}
          >
            Expenses
          </li>
        </ul>
      </div>
      <div className="list"></div>
    </Activity>
  );
};

export default Activities;
