const Activities = {

    activities_reset:{
        margin: 0,
        padding: 0,
        // backgroundColor: 'whitesmoke',
    },

    activities_header:{
        backgroundImage: `linear-gradient(120deg, #89f7fe 0%, #66a6ff 100%)`,
        // backgroundImage: `linear-gradient(135deg, #667eea 0%, #764ba2 100%)`,
        display: 'block',
        width: '500px',
        height: '450px',
        padding: '12px',
        margin: '25px 20px',
        borderRadius: '10px',
    },

    activities_h3: {
        fontSize: '20px',
        fontWeight: 'normal',
        fontStyle: 'normal',

    },

