    },

    activities_li:{
        listStyle: 'none',
        display: 'inline',
        margin: '12px',
    },

    activities_a: {
        padding: '5px 25px',
        margin: '5px',
        textDecoration: 'none',
        textTransform: 'capitalize',
        color: 'blue',
        backgroundColor: 'none',
         border: 'none',
        cursor: 'pointer',
    },
    activity_list: {
        margin: '65px 10px',
        display: 'block',
    },

    activity_list_div: {
        display: 'block',
        backgroundImage: `linear-gradient(to left, #BDBBBE 0%, #9D9EA3 100%), radial-gradient(88% 271%, rgba(255, 255, 255, 0.25) 0%, rgba(254, 254, 254, 0.25) 1%, rgba(0, 0, 0, 0.25) 100%), radial-gradient(50% 100%, rgba(255, 255, 255, 0.30) 0%, rgba(0, 0, 0, 0.30) 100%)`,
        backgroundBlendMode: 'normal, lighten, soft-light',
        opacit: '0.5px',
        color: 'white',
    },
    activity_list_h3: {
        textAlign: 'left',
        position: 'relative',
        top: '37px',
        paddingLeft: '12px',
        textTransform: 'uppercase',
        fontWeight: 'normal',
    },

    activity_list_p: {
        textAlign: 'center',
    },

    activity_list_date: {
        textAlign: 'right',
        position: 'relative',
        bottom: '33px',
        paddingRight: '12px',
    }
}

export default Activities;
