import styled from "styled-components";
// import { Link } from "react-router-dom";

export const Activities = styled.div`
  background-color: white;
  margin-left: 2em;

  width: 50%;
  .top-bar > h3 {
    text-align: center;
    font-weight: normal;
  }
  .top-bar {
    font-size: 1.2em;
  }

  .top-bar > ul {
    list-style: none;
    display: flex;
  }
  .top-bar > ul > li {
    display: inline;
  }
`;
