export const getAmount = (id, token) => {
  let url = `http://127.0.0.1:8000/api/getamounttransactions/${id}`;
  const headers = new Headers({
    Accept: "application/json",
    "Content-Type": "application/json",
    Authorization: `Bearer ${token}`,
  });
  const request = new Request(url, {
    method: "GET",
    headers: headers,
  });

  return async (dispatch) => {
    try {
      const response = await fetch(request);
      const resData = await response.json();
      if (resData["state"]) {
        dispatch({ type: "GET_BALANCE", payLoad: resData });
      }
    } catch (e) {
      toast.notify("Please check you internet connection and try again later", {
        duration: 5000,
      });
      dispatch({ type: "GET_BALANCE", payLoad: {} });
    }
  };
};
