import styled from "styled-components";

export const Container = styled.div`
  // background-color: red;
  color: black;
  position: relative;

  .modalItems {
    position: absolute;
    width: 100vw;
    height: 60vh;
    left: calc(-59vw + 40%);
    top: -7.5em;
    background: white;
    color: #000;
    z-index: 10;
    border-radius: 10px;
    overflow: auto;

    ::-webkit-scrollbar {
      width: 3px;
      background-color: #f5f5f5;
    }
    ::-webkit-scrollbar-thumb {
      border-radius: 10px;
      background-color: #141414;
      background-image: -webkit-linear-gradient(
        90deg,
        transparent,
        rgba(0, 0, 0, 0.4) 50%,
        transparent,
        transparent
      );
    }
  }

  .modalItems > .header {
    text-align: center;
    font-size: 1.5em;
    margin: 1.2em 0;
  }

  .items {
    display: flex;
    flex-direction: column;
    // justify-content: center;
    align-items: center;
    line-height: 1.8;
    color: #141414;
  }

  .transaction_list > ul {
    list-style: none;
  }

  .transaction_list > ul li {
    box-shadow: 0 5px 16px rgba(0, 0, 0, 0.2);
    padding: 20px;
    border-radius: 5px;
    width: 80%
    margin: 0.8em;
    text-align: center;
    // cursor: pointer;
  }
`;
