import React, {useEffect ,useState} from 'react';
import {useDispatch, useSelector} from 'react-redux';
import ClipLoader from 'react-spinners/ClipLoader';
import {login} from './redux/actions/actionCreator';

function App() {

  const dispatch = useDispatch();
  const select = useSelector((data) =>{
    return data;
  });

  const holdValues = {
    email : '',
    password: '',
    state: false,
  };

  const [state, setState] = useState(holdValues);

  let userEmail = e =>{

    holdValues.email = e.target.value;
    // console.log(holdValues);
  }

  let userPassword = e =>{
    holdValues.password = e.target.value;
    holdValues.state = true;

  }

  let submit = (e) =>{
    e.preventDefault();
    setState(holdValues);
    dispatch(login(state));
  }

  return (
    <div className="App">
     <h1>Financial Management System</h1>
     <form>
       <label>Email</label>
       <input type='text' name='email' placeholder='Email' onChange={userEmail} />
       <label>Password</label>
       <input type='password' name='password' placeholder='password' onChange={userPassword}/>
       <button onClick={submit}>submit</button>
     </form>
     {
      select.login.data ? '' : console.log(select.login.data) 
     }
    </div>
  );
}

export default App;
