import styled from "styled-components";
// import { Link } from "react-router-dom";
const media = {
  mobile: `@media(max-width: 40em)`,
  tablet: `@media(min-width: 40em) and (max-width: 70em)`,
  bigScreen: `(min-width: 50em)`,
};
export const BalanceStats = styled.div`
  background-color: #fff;
  width: 100%;
  .chartContainer {
    height: 50vh;
    margin-left: 2em;
    // margin-top: 2em;
    width: 50%;
    box-shadow: 5px 5px 15px rgba(0, 0, 0, 0.1);
  }

  ${media.mobile} {
    margin-top: 0;
    .chartContainer {
      height: 40vh;
      margin-left: 0;
      margin-top: 1em;
      width: 100% !important;
    }
    .chartContainer > p {
      text-align: center;
    }
  }

  ${media.bigScreen} {
    display: none;
  }
`;
