import React, {useState, useEffect} from 'react';
import ClipLoader from 'react-spinners/ClipLoader';
import {useDispatch, useSelector} from 'react-redux';
import activities from '../styles/activitiesStyle';
import {activitiesAction} from '../redux/actions/actionCreator';

const Activities = (props) =>{

    const [state, setState] = useState({active: false, data: null});
    const dispatch = useDispatch();

    const select = useSelector((e) =>{
        return e;
    });

    useEffect(() =>{
        dispatch(activitiesAction());
    }, [dispatch]);

    console.log(select);

    return(

<div style={activities.activities_reset}>

        <div style={activities.activities_header}>

     <h3 style={activities.activities_h3}>Recent Activities</h3>

            <ul style={activities.activities_ul}>
                <li style={activities.activities_li}><a style={activities.activities_a} href='/one'>All transaction</a></li>
                <li style={activities.activities_li} ><a style={activities.activities_a} href='/two'>Earnings</a></li>
                <li style={activities.activities_li} ><a style={activities.activities_a} href='/three'>Expenses</a></li>
            </ul>

                <div style={activities.activity_list}>
                  {/* {
                      !select.activity.activities ? '' : select.activity.activities.transaction.map((e) =>(
                        <div key={e.id} style={activities.activity_list_div}>
                        <h3 style={activities.activity_list_h3} >{e.transaction_name}</h3>
                      <p style={activities.activity_list_p} >K {e.transaction_ammount}</p>
                      <p style={activities.activity_list_date} >{e.transaction_date}</p>
                        </div>
                      ))
                  } */}

                  {
                      !select.activity.activities ? '' : setState((prevState) =>({
                            ...prevState,
                            data: select.activity.activities.transaction,
                      }))
                  }

                </div>
        </div>

</div>

    )
}

export default Activities;
