import styled from "styled-components";

export const Transaction = styled.div`
  position: absolute;
  border-radius: 5px 5px 0px 0px;
  background: #555;
  color: #fff;
  width: 50%;
  // height: 60%;
  right: 3em;
  top: 2em;

  :before {
    position: absolute;
    top: 10%;
    right: -5px;
    content: "";
    width: 0;
    height: 0;
    margin: -5px 0 0;
    border-left: solid 5px #555;
    border-bottom: solid 5px transparent;
    border-top: solid 5px transparent;
  }
  .contain ul {
    list-style: none;
  }
  .contain li {
    margin: 1.5em 0em;
    text-align: center;
    cursor: pointer;
  }
`;
