import React, { useRef, useEffect, useCallback } from "react";
import { useSpring, animated } from "react-spring";

import {
  Background,
  ModalWrapper,
  ModalCotent,
  CloseModal,
} from "../../../styledComponents/Dashboard/Modals/createTransactionModal";

const CreateTransaction = ({ state, setState }) => {
  const ref = useRef();
  const animation = useSpring({
    config: {
      duration: 350,
    },
    opacity: state.showModal ? 1 : 0,
    transform: state.showModal ? `translateY(0%)` : `translateY(-100%)`,
  });
  return (
    <div>
      {state.showModal ? (
        <Background
          ref={ref}
          onClick={() => {
            console.log("clicked");
          }}
        >
          <animated.div style={animation}>
            <ModalWrapper state={state}>
              <ModalCotent>
                <h1 className="header">Create Transaction</h1>
                <div className="uservalue">
                  <form>
                    <label>transaction name</label>
                    <input
                      type="text"
                      name="transaction_name"
                      placeholder="transaction name"
                    />
                    <label>transaction ammount</label>
                    <input
                      type="number"
                      name="transaction_ammount"
                      placeholder="transaction ammount"
                    />
                    <label>Transaction type</label>
                    <select name="transaction_type">
                      <option value="income">Income</option>
                      <option value="expentiture">Expentiture</option>
                    </select>
                    <div className="buttons">
                      <button className="create">Create Transaction</button>
                      <button>Cancel</button>
                    </div>
                  </form>
                </div>
              </ModalCotent>
              <CloseModal
                onClick={() => {
                  setState((prevstate) => ({
                    ...prevstate,
                    showModal: false,
                  }));
                }}
              >
                &times;
              </CloseModal>
            </ModalWrapper>
          </animated.div>
        </Background>
      ) : null}
    </div>
  );
};

export default CreateTransaction;
