import React, { useRef, useEffect, useCallback } from "react";
import { useSpring, animated } from "react-spring";

import {
  Background,
  ModalWrapper,
  ModalCotent,
  CloseModal,
} from "../../../styledComponents/Dashboard/Modals/createTransactionModal";

const CreateTransaction = ({ state, setState }) => {
  const ref = useRef();
  const animation = useSpring({
    config: {
      duration: 350,
    },
    opacity: state.showModal ? 1 : 0,
    transform: state.showModal ? `translateY(0%)` : `translateY(-100%)`,
  });
  return (
    <div>
      {state.showModal ? (
        <Background ref={ref}>
          <animated.div style={animation}>
            <ModalWrapper state={state}>
              <ModalCotent>Hello World</ModalCotent>
              <CloseModal
                onClick={() => {
                  setState((prevstate) => ({
                    ...prevstate,
                    showModal: false,
                  }));
                  console.log(state);
                }}
              >
                &times;
              </CloseModal>
            </ModalWrapper>
          </animated.div>
        </Background>
      ) : null}
    </div>
  );
};

export default CreateTransaction;
