import React, { useEffect, useState } from "react";
import ClipLoader from "react-spinners/ClipLoader";
import { useDispatch, useSelector } from "react-redux";
import {
  Background,
  ModalWrapper,
  ModalCotent,
  CloseModal,
} from "../../../styledComponents/Dashboard/Modals/viewProjectsModal";
import Cookies from "universal-cookie";
import Bin from "../../../images/bin.png";

const ViewProjects = (props) => {
  // user informatin stored in cookies
  const cookie = new Cookies();
  let userInfo = cookie.get("user info");
  let userData = { token: cookie.get("login"), id: userInfo[0].id };

  // Action dispatcher function
  const dispatch = useDispatch();
  const select = useSelector((e) => {
    return e;
  });

  // dispatches the action when the component renders
  useEffect(() => {
    dispatch(allActivities(userData));
  }, [dispatch]);

  // loader css overider
  const override = `
  display: block;
  margin: 0 auto;
  border-color: black;
  margin-top: 10px;
      `;

  return (
    <div>
      <Background>
        <ModalWrapper>
          <ModalCotent>
            <div>
              <h3>All Projects</h3>
            </div>
          </ModalCotent>
          <CloseModal
            onClick={() => {
              props.setState((prevState) => ({
                ...prevState,
                viewProjects: false,
              }));
            }}
          >
            &times;
          </CloseModal>
        </ModalWrapper>
      </Background>
    </div>
  );
};

export default ViewProjects;
