    }

    .nav_bar p {
      display: none;
    }

    .side_bar {
      display: none;
    }

    .container > label {
      display: inline-block;
      color: black;
      padding: 1em;
      font-size: 10em;
    }
    .image-container {
      display: none;
    }
  }
`;

