import styled from "styled-components";
// import { Link } from "react-router-dom";
const media = {
  mobile: `@media(max-width: 40em)`,
  tablet: `@media(min-width: 40em) and (max-width: 70em)`,
};
export const BalanceStats = styled.div`
  margin-top: 2em;

  ${media.mobile} {
    width: 2px;
    height: 1px;
  }
`;
