import styled from "styled-components";

export const Container = styled.div`
  //background-color: red;
  color: black;
  position: relative;

  .modalWrapper {
  }

  .modalContent {
    position: absolute;
    width: 100vw;
    height: 45vh;
    left: calc(-59vw + 40%);
    top: -3.5em;
    background: red;
    color: #000;
    z-index: 10;
    border-radius: 10px;
  }
  .modalContent > .header {
    text-align: center;
    font-size: 1.5em;
  }
  .uservalue > input {
    display: none;
  }
`;
