import styled from "styled-components";

export const Background = styled.div`
  width: 200%;
  height: 100%;
  background: rgba(0, 0, 0, 0.8);
  position: fixed;
  right: 15em;
  top: 6em;
  display: flex;
  justify-content: center;

  align-items: center;
`;

export const ModalWrapper = styled.div`
  width: 800px;
  height: 500px;
  box-shadow: 0 5px 16px rgba(0, 0, 0, 0.2);
  background: #fff;
  color: #000;
  display: grid;
  grid-taplate-colums: 1fr 1fr;
  position: relative;
  z-index: 10;
  border-radius: 10px;
`;

export const ModalCotent = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  line-height: 1.8;
  color: #141414;
`;

export const CloseModal = styled.div`
  cursor: pointer;
  position: absolute;
  font-size: 1.5em;
  top: 20px;
  right: 25px;
  width: 32px;
  height: 32px;
  padding: 0;
  z-index: 10;
`;
