import {FETCH_ACTIVITIES} from '../actions/actionCreator';

let initialState = [];

let activiesReducer = (state = initialState, action) =>{
    if(action.type === FETCH_ACTIVITIES){
        return{
            ...state,
            activities: action.payLoad,
        }
    }

    return state;
}

export default activiesReducer;
